import React, { useEffect, useState } from 'react'
import storage from "./images/Storage-PNG-Images 1.png";
const Home = () => {
    const [content, setContent] = useState([])

    useEffect(() => {
        fetch('/storage_units')
            .then(r => r.json())
        .then(data => setContent(data))
    },[])
    return (
      <>

        <div className="About-section">
          <img src={storage} alt="" />
          <p>
            Welcome to Storage-Centre!.We are the worldâ€™s best storage facility.
            We offer storage facilities at the best rates in town.Our facilities
            are secure with 24hr CCTV surveilance and fire proofed for any form
            of damage.We offer cold rooms, secure lock storage and ample space
            for any goods you have.BOOK WITH US NOW!!
          </p>
        </div>

            <h1 className="book">Book Now</h1>
        <div className="n">
          {content.map((cont) => (
            <div key={cont.id} className="container">
              <img src={cont.image_url} alt="container" />
              <div className="details">
                <p>
                  <strong>SIZE:</strong> {cont.size}
                </p>
                <p>
                  <strong>STATUS:</strong>{" "}
                  {cont.status ? "Available" : "Unavailable"}
                </p>
              </div>
              <p id="price">
                <strong>PRICE:</strong>Ksh {cont.price} /=
              </p>
            </div>
          ))}
        </div>
      </>
    );
}

export default Home
